import React,{useState} from 'react'



export default function Textformet(props) {

    const handleUpClick = ()=>{
        let newtext = text.toUpperCase();
        setText(newtext)
        props.showAlert("Convert to UpperCase","Success");
    //    eyta diye title change kora jabe document.title ="jahidul- UpperCase"
    }

    const handleLoClick = ()=>{
        let newtext = text.toLowerCase();
        setText(newtext)
        props.showAlert("Convert to LowerCase","Success");
    }


    const handleDeleteClick = ()=>{
        let newtext = "";
        setText(newtext)
        props.showAlert("Your Text is Delete","Success");
    }


    const handleCopy =()=>{
        let text = document.getElementById("myBox");
        text.select();
        navigator.clipboard.writeText(text.value);
        props.showAlert("Your Text is Copy","Success");
    }


    const handleSpaces =()=>{
        let newtext = text.split(/[ ]+/);
        setText(newtext.join(" "));
        props.showAlert("Removed Extra Spaces","Success");
      
    }



    const handleOnChange = (event)=>{
        setText(event.target.value);
    }

    const [text, setText] = useState('');
    
    return (

        <>
        <div className="container" style={{color: props.mode==='dark'? 'white':'black'}}>   
            <h1>{props.heading}</h1>  
              <div className="mb-3">
              <textarea className="form-control" value={text} onChange={handleOnChange} 
               style={{backgroundColor:props.mode==='dark'? 'gray':'white',color: props.mode==='dark'? 'white':'black'}}  id="myBox" rows="8">                 
              </textarea>
              </div>
              <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to Uppercase</button>
              <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to Lowercase</button>
              <button className="btn btn-primary mx-2" onClick={handleDeleteClick}>Delete Text</button>
              <button className="btn btn-primary mx-2" onClick={handleCopy}>Copy Text</button>
              <button className="btn btn-primary mx-2" onClick={handleSpaces}>Extra Space</button>
       


             
        </div>

        <div className="container my-3" style={{color: props.mode==='dark'? 'white':'black'}}>
            <h2>Your Text Summary</h2>
            <p> {text.split(" ").length} Words and {text.length}Characters</p>
            <p> {0.008 * text.split("").length} Minutes to Read </p>
            <h2>Preview</h2>
            <p>{text.length>0?text:"Enter something in the textbox above to preview it here"}</p>
        </div>
        </>

    )
}
