import './App.css';
import About from './components/About';
import Navbar from './components/Navbar';
import Textformet from './components/Textformet';
import React, { useState } from 'react';
import Alert from './components/Alert';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

function App() {

  const [mode, setmode] = useState('light');
  const [alert, setAlert] = useState(null);
  const showAlert = (message,type)=>{
    setAlert({
      msg :message,
      type: type
    })

    setTimeout(() => {
      setAlert(null);

    }, 1500);
  }

  const toggleMode = ()=>{
    if(mode === 'light'){
      setmode('dark');
      document.body.style.backgroundColor ="#706161";
      showAlert("Dark mode has been enabled","Success");
    }
    else{
      setmode('light');
      document.body.style.backgroundColor ="white";
      showAlert("Light mode has been enabled","Success");
    }
  }

  return (
       <>   
    <Router>   
            <Navbar title="jahidul"  aboutText="About" mode={mode} toggleMode={toggleMode} />
            <Alert alert={alert}/>
            <div className="container my-3">
            <Switch>

                <Route path="/about">
                  <About />       
                </Route>

                 <Route path="/">
                  <Textformet  showAlert={showAlert}     heading="Enter the Text to Analyze Below" mode={mode} />
                 </Route>

            </Switch>
            </div>
    </Router>
       </>

  );
}

export default App;
